def getIngredientsString():
    driver =  webdriver.Firefox(service=service)
    driver.get(url[0])
    time.sleep(60)
    allIngreditiensDetails = driver.find_element(by=By.XPATH, value="//*[@id='ingredients_page']/section/div/div[4]")
    print(allIngreditiensDetails.text.split("\n"))
    # //*[@id="ingredients_page"]/section/div/div[4]

def getIngredientsList(arr):
    inc = 0
    newIngString = []
    while True:
        try:
            if(len(arr) == inc):
                break
            if len(allIngreditiensDetails[inc]) > 50:
                newIngString.append(allIngreditiensDetails[inc])
        except:
            break
        inc+=1
    return newIngString

